mapper.select:
SELECT id
FROM tb_user
WHERE username = #{username}
  AND password = #{password}
    LIMIT 1;

SELECT username
FROM tb_user
WHERE id = #{userId}

mapper.update:
UPDATE tb_user
SET password = #{newPassword}
WHERE id = #{userId}
  AND password = #{password}

